$(document).ready(function(){
    var url = '/prmtcenter/orderDetail/queryDeliveryInfo.htm';
    var data = {
        'orderCode': $.getQueryString('orderid'),
        'orderType': $.getQueryString('orderType')?$.getQueryString('orderType'):'0'
    };
    $.postajax(url, data, function(res){
        var resData = res.data;
        var deliveryStatus = '';
        switch(resData.deliveryInfo.deliveryStatus){
            case 0:
            deliveryStatus='在途';
            break;
            case 1:
            deliveryStatus='揽件';
            break;
            case 2:
            deliveryStatus='疑难';
            break;
            case 3:
            deliveryStatus='签收';
            break;
            case 4:
            deliveryStatus='退签';
            break;
            case 5:
            deliveryStatus='派件';
            break;
            case 6:
            deliveryStatus='退回';
            break;
            // 0 在途 1：揽件 2：疑难 3：签收 4：退签 5：派件 6：退回
        }
        var deliveryData = {
            deliveryStatus: deliveryStatus,
            deliveryCompanyName: resData.deliveryCompanyName,
            deliveryNo: resData.deliveryInfo.deliveryNo,
            deliveryArr: resData.deliveryInfo.deliveryRecordItemVOList
        }
        if(resData.deliveryInfo.deliveryStatus) {
            var source = $("#entry-template").html();
            var template = Handlebars.compile(source);
            $(".delivery").append(template(deliveryData));
        } else {
            $(".delivery").hide();
        }
    })
})

    